import { takeLatest, call, put, all } from "@redux-saga/core/effects";
import ShopActionType from "./shops.types";

import {
  firestore,
  convertCollectionsSnapshotToMap,
} from "../../firebase/firebase.utils";

import {
  fetchCollectionsSuccess,
  fetchCollectionsFailure,
} from "./shop.actions";

export function* fetchCollectionsStartAsync() {
  try {
    // get a reference to the collections collection
    const collectionRef = firestore.collection("collections");
    // returns a promise when promise is resolve, we get snapshot
    const snapshot = yield collectionRef.get();
    console.log(
      "ðŸš€ ~ file: shop.sagas.js ~ line 20 ~ function*fetchCollectionsStartAsync ~ snapshot",
      snapshot
    );

    const collectionsMap = yield call(
      convertCollectionsSnapshotToMap,
      snapshot
    );
    yield put(fetchCollectionsSuccess(collectionsMap));
  } catch (error) {
    yield put(fetchCollectionsFailure(error.message));
  }
}
export function* fetchCollectionsStart() {
  yield takeLatest(
    ShopActionType.FETCH_COLLECTIONS_START,
    fetchCollectionsStartAsync
  );
}

export function* shopSagas() {
  yield all([call(fetchCollectionsStart)]);
}
