import styled from "styled-components";

export const MenuItemContainer = styled.div`
  height: 350px;
  display: flex;
  width: 30%;
  flex: 1 1 auto;
  align-items: center;
  justify-content: center;

  border: 1px solid black;

  overflow: hidden;
  margin: 0 7.5px 15px;
`;

